pragma solidity ^0.8.0;

contract BlockchainMessenger {
    // Define a struct to store the message data
    struct Message {
        string content;
        uint256 count;
        address author;
    }

    // Define a mapping to store the messages, indexed by their hash
    mapping(uint256 => Message) private messages;

    // Define a function to store a message
    function storeMessage(string memory content) public {
        // Calculate the hash of the message content
        uint256 messageHash = keccak256(abi.encodePacked(content));

        // Check if the message already exists
        require(messages[messageHash].count == 0, "Message already exists");

        // Store the message and increment the count
        messages[messageHash] = Message(content, 1, msg.sender);
    }

